export function getProductBySlug(slug: string): Product | undefined {
  return products.find(p => p.slug === slug);
}

export function getProductsByCategory(categorySlug: string): Product[] {
  return products.filter(p => p.categorySlugs.includes(categorySlug));
}

export function getProductsOnSale(): Product[] {
  return products.filter(p => p.salePrice && p.salePrice !== '' && p.price !== p.salePrice);
}

export function getNewProducts(): Product[] {
  return products.filter(p => p.categorySlugs.includes('new-products'));
}
